<template>
  <button
    :is="to ? 'router-link' : 'button'"
    :to="to"
    :disabled="disabled"
    :type="type"
    :class="getClass"
    class="vf-a-button"
    v-on="$listeners"
  >
    <slot></slot>
    <span v-if="$slots.visible" class="btn-inner--visible">
      <slot name="visible"></slot>
    </span>
    <span v-if="$slots.hidden" class="btn-inner--hidden">
      <slot name="hidden"></slot>
    </span>
  </button>
</template>
<script>
export default {
  props: {
    block: {
      type: Boolean,
      default() {
        return false;
      },
    },
    animatedY: {
      type: Boolean,
      default() {
        return null;
      },
    },
    animatedX: {
      type: Boolean,
      default() {
        return null;
      },
    },
    rounded: {
      type: Boolean,
      default() {
        return null;
      },
    },
    type: {
      type: String,
      default() {
        return "button";
      },
    },
    active: {
      type: Boolean,
      default() {
        return null;
      },
    },
    outline: {
      type: Boolean,
      default() {
        return null;
      },
    },
    size: {
      type: String,
      default() {
        return "md";
      },
    },
    pressed: {
      type: Boolean,
      default() {
        return false;
      },
    },
    color: {
      type: String,
      default() {
        return "primary";
      },
    },
    to: {
      type: [String, Object],
      default() {
        return null;
      },
    },
    nuxt: {
      type: Boolean,
      default() {
        return false;
      },
    },
    disabled: {
      type: Boolean,
      default() {
        return false;
      },
    },
  },
  data() {
    return {
      error: false,
    };
  },
  computed: {
    getClass() {
      const result = [];

      if (this.disabled) {
        result.push("disabled:opacity-50");
      }
      if (this.active || this.pressed) {
        result.push("active");
      }
      if (this.outline) {
        result.push(`vf-a-button--outline-${this.color}`);
      } else {
        result.push(`vf-a-button--${this.color}`);
      }

      if (this.block) {
        result.push("w-full");
      }

      result.push(`vf-a-button--${this.size}`);

      if (this.rounded) {
        result.push("vf-a-button--rounded");
      }

      if (this.animatedX || this.animatedY) {
        result.push("vf-a-button--animated");
      }

      if (this.animatedX) {
        result.push("vf-a-button--animated-x");
      }

      if (this.animatedY) {
        result.push("vf-a-button--animated-y");
      }

      return result.join(" ");
    },
  },
};
</script>
